package diagrama.de.flujo;

public class main3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//imprimir la tabla del 7 multiplicado hasta el 12
		
		int n = 7;
		System.out.println("Tabla del 7");
		
		for(int i=0 ; i==12 ; i++)
			System.out.println(i + "*" + n+ "=" + i*n);
		
	}

}
